import React, { Component } from 'react'
import Newsitem from './Newsitem'

export class News extends Component {
  
    constructor(){
        super();
        this.state = {
            articles:[],
            loading: false,
            page:1
        }
    }
    nextPage=()=>{
      this.setState(page+=1)
    }
    prevPage=()=>{
      this.setState(page-=1)
    }
    async componentDidMount(){
        let url = 'https://newsapi.org/v2/top-headlines?country=in&category=sports&apiKey=4bd35ae40cd44023b7390d3df1b4411e'
        let data = await fetch(url);
        let parsedData = await data.json();
        this.setState({articles : parsedData.articles})
    }
  render() {
    return (
      <div className='container my-3'>
        <h2>News Monkey- Top Headlines</h2>
        <div className="row">
            {this.state.articles.map((element)=>{
                return <div className="col-md-4" key= {element.url}>
                    <Newsitem title={element.title?element.title:''} description = {element.description?element.description:''} imgUrl={element.urlToImage}
                    newsUrl = {element.url}/>
                        </div>
            })}
        </div>
        <div className="container my-3 d-flex justify-content-between">
        <button type="button" onClick = {prevPage} class="btn btn-dark">&larr; Prev</button>
        <button type="button" onClick = {nextPage} class="btn btn-dark">Next &larr;</button>
        </div>
        
      </div>
    )
  }
}

export default News
