import React, { Component } from 'react'
import Newsitem from './Newsitem'
import Spinner from './Spinner';

export class News extends Component {

  static defaultProps={
    country: 'in',
    pageSize: 8
  }
  static propTypes={
    country : PropTypes.string,
    pageSize : ProTypes.number
  }
  
    constructor(){
        super();
        this.state = {
            articles:[],
            loading: false,
            page:1
        }
    }
    nextPage=async()=>{
      if(!this.state.page+1 > Math.ceil(this.state.totalResults/this.props.pageSize)){
        let url = `https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=sports&apiKey=4bd35ae40cd44023b7390d3df1b4411e&page=${this.state.page+=1}&pageSize=${this.props.pageSize}`
        this.setState ({loading : true});
        let data = await fetch(url);
        let parsedData = await data.json();   
        this.setState({
          page: this.state.page+=1,
          articles : parsedData.articles,
          loading : false
        })
      }
    }
    prevPage= async()=>{
      let url = `https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=sports&apiKey=4bd35ae40cd44023b7390d3df1b4411e&page=${this.state.page-=1}&pageSize=${this.props.pageSize}`
      this.setState ({loading : true});
      let data = await fetch(url);
      let parsedData = await data.json();   
      this.setState({
        page: this.state.page-=1,
        articles : parsedData.articles,
        loading : false
      })
    }
    async componentDidMount(){
      let url = `https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=sports&apiKey=4bd35ae40cd44023b7390d3df1b4411e&page=${this.state.page}&pageSize=${this.props.pageSize}`
      this.setState ({loading : true});
      let data = await fetch(url);
      let parsedData = await data.json();
      this.setState({
        articles : parsedData.articles, 
        totalResults: parsedData.totalResults,
        loading : false
      })
    }
  render() {
    return (
      <div className='container my-3'>
        <h2 className='text-center'>News Monkey- Top Headlines</h2>
        {this.state.loading && <Spinner/>}
        <div className="row">
            {!this.state.loading&&this.state.articles.map((element)=>{
                return <div className="col-md-4" key= {element.url}>
                    <Newsitem title={element.title?element.title:''} description = {element.description?element.description:''} imgUrl={element.urlToImage}
                    newsUrl = {element.url}/>
                        </div>
            })}
        </div>
        <div className="container my-3 d-flex justify-content-between">
        <button disabled={this.state.page<=1} type="button" onClick = {this.prevPage} className="btn btn-dark">&larr; Prev</button>
        <button disabled ={this.state.page+1 > Math.ceil(this.state.totalResults/this.props.pageSize)}type="button" onClick = {this.nextPage} className="btn btn-dark">Next &rarr;</button>
        </div>
        
      </div>
    )
  }
}

export default News
